window.cancionesCatalogo = cancionesCatalogo;

// Función para cargar canciones en una sección específica
function cargarCancionesEnSeccion(seccionId, seccionNombre) {
  const seccion = document.getElementById(seccionId);
  const cancionesFiltradas = cancionesCatalogo.filter(
    (c) => c.seccion === seccionNombre
  );
  cancionesFiltradas.forEach((cancion) => {
    const div = document.createElement("div");
    div.classList.add("cancion");
    div.setAttribute("data-src", cancion.ruta);
    div.innerHTML = `
      <img src="${cancion.thumbnail}" alt="Portada de la canción ${cancion.titulo}">
      <p>${cancion.titulo}</p>
    `;
    // Hacer la canción clickeable para el reproductor
    div.addEventListener("click", () => {
      window.parent.frames["reproductorFrame"].postMessage(
        { song: cancion.ruta },
        "*"
      );
    });
    seccion.appendChild(div);
  });
}

// función: buscar canciones
function buscarCanciones(query) {
  query = query.toLowerCase();
  return cancionesCatalogo.filter((c) =>
    c.titulo.toLowerCase().includes(query)
  );
}
